{"changed":true,"filter":false,"title":"menu_controller.rb","tooltip":"/controllers/menu_controller.rb","value":" require_relative '../models/address_book'\n \n class MenuController\n   attr_reader :address_book\n \n   def initialize\n     @address_book = AddressBook.new\n   end\n \n   def main_menu\n     # #2\n     puts \"Main Menu - #{address_book.entries.count} entries\"\n     puts \"1 - View all entries\"\n     puts \"2 - View Entry Number n\"\n     puts \"3 - Create an entry\"\n     puts \"4 - Search for an entry\"\n     puts \"5 - Import entries from a CSV\"\n     puts \"6 - Exit\"\n     print \"Enter your selection: \"\n \n     # #3\n     selection = gets.to_i\n\n    case selection\n       when 1\n         system \"clear\"\n         view_all_entries\n         main_menu\n       when 2\n         system \"clear\"\n         view_entry_number\n         main_menu\n       when 3\n         system \"clear\"\n         create_entry\n         main_menu\n       when 4\n         system \"clear\"\n         search_entries\n         main_menu\n       when 5\n         system \"clear\"\n         read_csv\n         main_menu\n       when 6\n         puts \"Good-bye!\"\n         # #8\n         exit(0)\n       # #9\n       else\n         system \"clear\"\n         puts \"Sorry, that is not a valid input\"\n         main_menu\n    end\n\n \n   # #10\n   def view_all_entries\n       address_book.entries.each do |entry|\n       system \"clear\"\n       puts entry.to_s\n     # #15\n       entry_submenu(entry)\n     end\n \n     system \"clear\"\n     puts \"End of entries\"\n   end\n   \n   def view_entry_number\n       print \"Entry number to view: \"\n       selection = gets.chomp.to_i\n       \n       if selection < @address_book.entries.count\n           puts @address_book.entries[selection]\n           puts \"Press enter to return to the main menu\"\n           gets.chomp\n           system \"clear\"\n       else\n           puts \"#{selection} is not a valid input\"\n           view_entry_number\n       end\n           \n   end\n \n   def create_entry\n     system \"clear\"\n     puts \"New AddressBloc Entry\"\n     # #12\n     print \"Name: \"\n     name = gets.chomp\n     print \"Phone number: \"\n     phone = gets.chomp\n     print \"Email: \"\n     email = gets.chomp\n \n     # #13\n     address_book.add_entry(name, phone, email)\n \n     system \"clear\"\n     puts \"New entry created\"\n   end\n \n   def search_entries\n   end\n \n   def read_csv\n       \n   end\n  \n     def entry_submenu(entry)\n     # #16\n     puts \"n - next entry\"\n     puts \"d - delete entry\"\n     puts \"e - edit this entry\"\n     puts \"m - return to main menu\"\n \n     # #17\n     selection = gets.chomp\n \n     case selection\n     # #18\n       when \"n\"\n     # #19\n       when \"d\"\n       when \"e\"\n     # #20\n       when \"m\"\n         system \"clear\"\n         main_menu\n       else\n         system \"clear\"\n         puts \"#{selection} is not a valid input\"\n         entry_submenu(entry)\n     end\n     end\n   end\n end","undoManager":{"mark":-215,"position":100,"stack":[[{"start":{"row":75,"column":42},"end":{"row":75,"column":43},"action":"insert","lines":["t"],"id":358}],[{"start":{"row":75,"column":43},"end":{"row":75,"column":44},"action":"insert","lines":["h"],"id":359}],[{"start":{"row":75,"column":44},"end":{"row":75,"column":45},"action":"insert","lines":["e"],"id":360}],[{"start":{"row":75,"column":45},"end":{"row":75,"column":46},"action":"insert","lines":[" "],"id":361}],[{"start":{"row":75,"column":46},"end":{"row":75,"column":47},"action":"insert","lines":["m"],"id":362}],[{"start":{"row":75,"column":47},"end":{"row":75,"column":48},"action":"insert","lines":["a"],"id":363}],[{"start":{"row":75,"column":48},"end":{"row":75,"column":49},"action":"insert","lines":["i"],"id":364}],[{"start":{"row":75,"column":49},"end":{"row":75,"column":50},"action":"insert","lines":["n"],"id":365}],[{"start":{"row":75,"column":50},"end":